        public static int ParseParens(string input)
        {
            if (string.IsNullOrEmpty(input))
                return 0;
            var inputArray = input.ToCharArray();
            int answer = 0;
            foreach (char t in inputArray)
            {
                if (t.Equals('('))
                {
                    answer++;
                }
                else
                {
                    answer--;
                }

            }

            return answer;
        }


        public static int FindFirstBasement(string input)
        {
            if (string.IsNullOrEmpty(input))
                return -1;
            var inputArray = input.ToCharArray();
            int answer = 0;
            for(int i = 0; i < inputArray.Length; i++ )
            {
                if (inputArray[i].Equals('('))
                {
                    answer++;
                }
                else
                {
                    answer--;
                }

                if (answer == -1)
                {
                    return i + 1;
                }

            }

            return -1;
        }
